+    87class FinalizeGroup extends Component {
+    88  static navigationOptions = ({ navigation }) => {
+    89    const { state } = navigation;
+    90    const isReady = state.params && state.params.mode === 'ready';
+    91    return {
+    92      title: 'New Group',
+    93      headerRight: (
+    94        isReady ? <Button
+    95          title="Create"
