 ┊152┊152┊      mutate({
 ┊153┊153┊        variables: { text, userId, groupId },
+┊   ┊154┊        update: (store, { data: { createMessage } }) => {
+┊   ┊155┊          // Read the data from our cache for this query.
+┊   ┊156┊          const groupData = store.readQuery({
+┊   ┊157┊            query: GROUP_QUERY,
